import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import {Quotebox} from './Components/Quotebox.js'
import { connect } from 'react-redux'

function App(props ) {

    const [color, setColor] = useState([...props.colorarray]); //initializing state from redux store values
    var [colornow , setColornow] = useState(props.colornow)     //

    const [wrapperclass, setWrapperclass] = useState('wrapper')
    const mystyle = {
        backgroundColor: color[colornow],
        color: color[colornow]
    };

    function colorchange() {
        if(colornow >= color.length) {
           // colornow = 0
        } 
        setWrapperclass('wrapperClass')
        setTimeout(function(){
            setWrapperclass('wrapper')
            this.props.dispatch({
                type: 'CHANGECOLORNOW',
                payload: { id: this.state.postId, title: this.state.value }
              })
            // setColornow(colornow+1)

        },10)
    }

    return (<>
        <div className={wrapperclass} style={mystyle}>
            <Quotebox color={color[colornow]} colorchange={colorchange} ></Quotebox>
        </div>
        </>
    );
}

const mapStateToProps = state => {
    return { colorarray: state.color , colornow: state.colornow }
  }

const mapDispatchToProps = dispatch => {
return {
    dispatch
}
}

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(App)
