import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import {Quotebox} from './Components/Quotebox.js'
import { connect } from 'react-redux'

function App({ props }) {
    // const [color, setColor] = useState([
    //     '#16a085',
