    //     '#342224',
    //     '#472E32',
    //     '#BDBB99',
    //     '#77B1A9',
    //     '#73A857'
    //   ]);

    const [wrapperclass, setWrapperclass] = useState('wrapper')
    var [colornow , setColornow] = useState(0)
    const mystyle = {
        backgroundColor: props.color[props.colornow],
        color: props.color[props.colornow]
    };

    function colorchange() {
        if(props.colornow >= props.color.length) {
            colornow = 0
        } 
        setWrapperclass('wrapperClass')
        setTimeout(function(){
            setWrapperclass('wrapper')
            setColornow(colornow+1)

        },10)
    }

    return (<>
        <div className={wrapperclass} style={mystyle}>
            <Quotebox color={props.color[props.colornow]} colorchange={colorchange} ></Quotebox>
            <p>{posts}</p>
        </div></>
    );
}

const mapStateToProps = state => {
    return { color: state.color , colornow: state.colornow , mystyle: state.mystyle }
  }

  export default connect(mapStateToProps)(App)
